        private void dgvhanghoa_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                if (this.dgvhanghoa.RowCount > 0)
                {
                    int columnIndex = this.dgvhanghoa.CurrentCell.ColumnIndex;
                    if (this.dgvhanghoa.Columns[columnIndex].Name == "tenhang")
                    {
                        this.kiem = this.luukiem;
                        while (this.kiem < this.dgvhanghoa.Rows.Count)
                        {
                            if (this.dgvhanghoa["tenhang", this.kiem].Value.ToString().Substring(0, 1).ToUpper() == e.KeyCode.ToString())
                            {
                                this.dgvhanghoa.ClearSelection();
                                this.dgvhanghoa.Rows[this.kiem].Selected = true;
                                this.dgvhanghoa.CurrentCell = this.dgvhanghoa.Rows[this.kiem].Cells["tenhang"];
                                this.luukiem = this.kiem + 1;
                                break;
                            }
                            this.luukiem = this.kiem;
                            this.kiem++;
                        }
                        if (this.kiem == this.dgvhanghoa.RowCount)
                        {
                            this.luukiem = 0;
                        }
                    }
                    if (e.KeyCode == Keys.Delete)
                    {
                        Frmyesno.caption = "THÔNG BÁO XÓA";
                        Frmyesno.text = "Bạn có muốn xóa " + this.dgvhanghoa.CurrentRow.Cells["tenhang"].Value + " không?";
