                        frmyesno.ShowDialog();
                        if (Frmyesno.dongy == "ok")
                        {
                            this.dgvhanghoa.Rows.RemoveAt(this.dgvhanghoa.CurrentRow.Index);
                            this.tinhtinhtong();
                            this.txtmahang.Focus();
                        }
                    }
                }
            }
            catch
