        private void txtmahang_KeyPress(object sender, KeyPressEventArgs e)
        {
            if (this.chiquet_mavach == "True")
            {
                this.timkiem_ten = 1;
            }
            else if (TrangChu.scan == "True")
            {
                if (char.IsDigit(e.KeyChar))
                {
                    if (this.txtmahang.Text.Length > 0 && this.txtmahang.Text.Substring(0, 1) == " ")
                    {
                        this.timkiem_ten = 0;
                    }
                    else
                    {
                        this.timkiem_ten = 1;
                    }
                }
                else
                {
                    this.timkiem_ten = 0;
                }
            }
            else
            {
                this.timkiem_ten = 0;
            }
        }

